	add_action('gdlr_core_footer_widget_args', 'arki_gdlr_core_footer_widget_args');
	if( !function_exists('arki_gdlr_core_footer_widget_args') ){
		function arki_gdlr_core_footer_widget_args($atts){
			$footer_title_divider = arki_get_option('general', 'footer-title-divider', 'disable');

			if( $footer_title_divider == 'enable' ){
				$sidebar_heading_tag = arki_get_option('general', 'sidebar-heading-tag', 'h3');

				$atts['before_title']  = '<' . $sidebar_heading_tag . ' class="arki-widget-title arki-with-divider">';
				$atts['after_title']  = '</' . $sidebar_heading_tag . '><span class="clear"></span>';
			} 
			

			return $atts;
		}
	}

	// youtube no cookies
	if( !function_exists('arki_gdlr_core_privacy_settings') ){
		function arki_gdlr_core_privacy_settings(){
			if( function_exists('gdlr_core_get_privacy_options') ){
				$privacy_options = gdlr_core_get_privacy_options(1) + gdlr_core_get_privacy_options(2);
				$privacy_settings = array();
				foreach( $privacy_options as $privacy_option_slug => $privacy_option ){
					$privacy_settings[$privacy_option_slug] = arki_get_option('plugin', $privacy_option_slug, 
						(empty($privacy_option['default'])? '': $privacy_option['default']));
				}

				return $privacy_settings;
			}
			
			return array();
		}
	}

