	// custom post type slug
	if( !function_exists('arki_gdlr_core_custom_post_slug') ){
		function arki_gdlr_core_custom_post_slug( $slug, $type = '' ) {
			if( empty($type) ){ $type = $slug; }

			if( $type == 'portfolio' ){
