			}else if( $type == 'portfolio_category' ){
				$slug = arki_get_option('general', 'portfolio-category-slug', $slug);
			}else if( $type == 'portfolio_tag' ){
				$slug = arki_get_option('general', 'portfolio-tag-slug', $slug);
			}else if( $type == 'personnel' ){
				$slug = arki_get_option('general', 'personnel-slug', $slug);
			}else if( $type == 'personnel_category' ){
				$slug = arki_get_option('general', 'personnel-category-slug', $slug);
			}
			
			return $slug;
		}
	}

